INSERT INTO livro VALUES(140,'2018','Frederico Lourenço','Poesia','A Odisseia não é apenas um dos grandes épicos da literatura grega; é também um dos pilares do cânone ocidental, um poema de rara e extraordinária beleza - e o livro que mais influência exerceu, ao longo dos tempos, no imaginário ocidental.',
